    const [newCurrency, setCurrency] = useState('£ Pound')

    const UpdateCurrency = (event) => {
        if (cost > remaining) {
            alert("The value cannot exceed remaining funds £ " + remaining);

            setCost('');
            return;
        }

        const expense = {
            name: name,
            cost: parseInt(cost),
        };
        if (action === "Reduce") {
            dispatch({
                type: 'RED_EXPENSE',
                payload: expense,
            });
        } else {
            dispatch({
                type: 'ADD_EXPENSE',
                payload: expense,
            });
        }
    }
    return (
        <div className="alert alert-primary">
            <span>Currency</span>
            <select className="custome-select" id="inputGroupSelect01" onChange={UpdateCurrency}>
                <option defaultValue value='£ Pound' name='Pound'>£ Pound</option>
                <option value="$ Dollar" name='Dollar'>$ Dollar</option>
                <option value="$ Dollar" name='Dollar'>$ Dollar</option>
                <option value="$ Dollar" name='Dollar'>$ Dollar</option>
            </select>
        </div>
    );
};

export default Currency;
